--
-- Structure de la table `SymfonyUser`
--

CREATE TABLE `SymfonyUser` (
  `id` int(11) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `surname` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `username` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `password` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `salt` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `roles` longtext COLLATE utf8_unicode_ci COMMENT '(DC2Type:array)',
  `email` varchar(60) COLLATE utf8_unicode_ci NOT NULL,
  `subscription_date` datetime NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Déchargement des données de la table `SymfonyUser`
--

INSERT INTO `SymfonyUser` (`id`, `name`, `surname`, `username`, `password`, `salt`, `roles`, `email`, `subscription_date`) VALUES
